SELECT
		nr_atendimento,
		dt_entrada,
		dt_alta,
		obter_dias_internacao(nr_atendimento) dias_internado,
		obter_cid_atendimento(nr_atendimento,'P') CID,
		obter_dados_pf(cd_pessoa_fisica,'I') idade,
		obter_faixa_etaria_pf(cd_pessoa_fisica) faixa_etaria,
		obter_dados_pf(cd_pessoa_fisica,'KG') peso,
		obter_dados_pf(cd_pessoa_fisica,'SE') sexo,
		obter_dados_pf(cd_pessoa_fisica,'CNP') cidade_natal,
		obter_ds_setor_atendimento(obter_setor_atendimento(nr_atendimento)) setor_atendimento,
		obter_desc_procedencia(cd_procedencia) ds_procedencia,
		obter_desc_motivo_alta(cd_motivo_alta_medica) motivo_alta
		
		
FROM atendimento_paciente
WHERE dt_entrada BETWEEN '01/03/2020' AND fim_dia('30/09/2020')
AND IE_TIPO_ATENDIMENTO = 1
